org.jenkinsci.plugins.managedscripts.Scr.content:
#!/bin/bash

cd "${WORKSPACE}";
export ARTIFACT="${1}";
if [ ! -d target ]; then mkdir target; fi;
/opt/apache-maven/bin/mvn -o versions:update-parent | grep "Updating parent from " | tail -n 1 | cut -d' ' -f 7 | awk -F'.' '{printf("%s.%s.%04d\n", $1, $2, $3);}' > "${WORKSPACE}"/target/.version;
echo "** VERSION: $(cat "${WORKSPACE}"/target/.version)";
git checkout -b ${ARTIFACT}-$(cat "${WORKSPACE}"/target/.version);

#!/bin/bash

cd "${WORKSPACE}";

_group="$1";
if [ "x${_group}" == "x" ]; then
  echo "Group is mandatory";
  exit 1;
else
  shift;
fi
_artifact="$1";
if [ "x${_artifact}" == "x" ]; then
  echo "Artifact is mandatory";
  exit 1;
else
  shift;
fi
_repo="$1";
if [ "x${_repo}" == "x" ]; then
  echo "Repo is mandatory";
  exit 1;
else
  shift;
fi
_version="$(cat "${WORKSPACE}"/target/.version)";
if [ "x${_version}" == "x" ]; then
  echo "Version is mandatory";
  exit 1;
fi

cat <<EOF > release.properties
scm.commentPrefix=
pushChanges=true
dependency.${_group}\:${_artifact}.development=latest-SNAPSHOT
project.scm.${_group}\:${_artifact}.connection=scm\:git\:${_repo}
scm.tag=${_artifact}-${_version}
scm.tagBase=${_repo}
remoteTagging=true
exec.additionalArguments=
project.dev.${_group}\:${_artifact}=latest-SNAPSHOT
project.scm.${_group}\:${_artifact}.tag=HEAD
scm.url=scm\:git\:${_repo}
scm.tagNameFormat=@{project.artifactId}-@{project.version}
commitByProject=true
preparationGoals=clean verify
project.scm.${_group}\:${_artifact}.url=${_repo}
exec.snapshotReleasePluginAllowed=false
project.scm.${_group}\:${_artifact}.developerConnection=scm\:git\:${_repo}
dependency.${_group}\:${_artifact}.release=${_version}
project.rel.${_group}\:${_artifact}=${_version}
completedPhase=end-release
EOF

#!/bin/bash

cd "${WORKSPACE}";
export ARTIFACT="${1}";
git add pom.xml */pom.xml && git commit -m"Releasing $(cat "${WORKSPACE}"/target/.version)" && \
git tag -a "${ARTIFACT}-$(cat "${WORKSPACE}"/target/.version)" -m"Releasing $(cat "${WORKSPACE}"/target/.version)" && git push origin --tags;
export NEW_GIT_TAG="${ARTIFACT}-$(cat "${WORKSPACE}"/target/.version)";
